import { useState, useEffect } from "react";
import axios from "axios";
import "../App.css";

function StrukturStaff() {
  const [staff, setStaff] = useState([]); // State untuk menyimpan data staff
  const [loading, setLoading] = useState(true); // State untuk status loading
  const [error, setError] = useState(null); // State untuk error jika ada

  // Fetch data dari API saat komponen pertama kali di-mount
  useEffect(() => {
    axios
      .get("http://localhost:8000/api/staff") // Ganti dengan URL API yang sesuai
      .then((response) => {
        console.log(response.data); // Cek format data yang diterima
        if (Array.isArray(response.data)) {
          setStaff(response.data); // Simpan data staff ke dalam state
        } else {
          setStaff([]); // Jika bukan array, set ke array kosong
        }
        setLoading(false); // Set loading menjadi false setelah data diambil
      })
      .catch((err) => {
        setError("Failed to fetch staff data"); // Tangani error jika terjadi
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>; // Tampilkan loading saat data sedang diambil
  if (error) return <p>{error}</p>; // Tampilkan error jika terjadi

  return (
    <div className="w-screen-xl px-4 py-8 sm:px-6 sm:py-12 lg:px-8 bg-gray-100">
      <div className="max-w-3xl sm:mx-48">
        <span className="flex items-center">
          <span className="pr-6">Diskominfo Kota Lhokseumawe</span>
          <span className="h-px flex-1 bg-black"></span>
        </span>
        <h1 className="sm:text-5xl font-bold text-2xl">Pimpinan</h1>
      </div>

      <dl className="mt-6 grid grid-cols-1 gap-4 sm:mt-8 sm:grid-cols-2 lg:grid-cols-4">
        {staff.length === 0 ? (
          <p>No staff available</p>
        ) : (
          staff.map((member) => (
            <article
              key={member.id}
              className="relative overflow-hidden rounded-lg shadow transition hover:shadow-lg">
              <img
                alt={member.name}
                src={member.photoUrl}
                className="absolute inset-0 h-full w-full object-cover"
              />

              <div className="relative bg-gradient-to-t from-gray-900/50 to-gray-900/25 pt-32 sm:pt-48 lg:pt-64">
                <div className="p-4 sm:p-6">
                  <time
                    datetime="2022-10-10"
                    className="block text-xs text-white/90">
                    {member.position}
                  </time>

                  <a href="/">
                    <h3 className="mt-0.5 text-lg text-white">{member.name}</h3>
                  </a>

                  <p className="mt-2 line-clamp-3 text-sm/relaxed text-white/95">
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                  </p>
                </div>
              </div>
            </article>
          ))
        )}
      </dl>
    </div>
  );
}

export default StrukturStaff;
